<script>
import HotelDatePicker from 'vue-hotel-datepicker';
import moment from 'moment';
export default {
    components: {
        HotelDatePicker
    },
    props: ['dataType', 'dataLocation','dataCheckin','dataCheckout'],
    data() {
        return {
            checkIn: '',
            checkOut: '',
            location: '',
            type: '',
            propName: '',
            numAvailable: null,
            searchIsOpen: false,
            filterIsOpen: false,
            nameIsOpen: false,
            showNameButton: true,
            filters:  {
                dock: false,
                pool: false,
                canal: false,
                internet: false,
                linens: false,
                pets: false
            }
        }
    },
    mounted () {
        // wait for data to be loaded in order to change the search bar
        setTimeout(() => {
            this.type = this.dataType;
            this.location = this.dataLocation;
            if(this.dataCheckin !='' && this.dataCheckout != ''){
                this.$refs.datepicker._data.checkIn = new Date(this.dataCheckin);
                this.$refs.datepicker._data.checkOut = new Date(this.dataCheckout);
            }
        }, 200);

    },
    methods: {
        checkInChanged(date) {
            this.checkIn = moment(date).format("YYYY-MM-DD");
            this.getMatches();
        },
        checkOutChanged(date) {
            this.checkOut = moment(date).format("YYYY-MM-DD");
            this.getMatches();
        },
        getMatches() {
            let url = 'https://rns.mexicobeachvacations.com/matches';

            axios.get(url, {
                params: {
                    type: this.type,
                    checkIn: this.checkIn,
                    checkOut: this.checkOut,
                    dock: this.filters.dock,
                    location: this.location,
                    pets: this.filters.pets,
                    pool: this.filters.pool,
                    canal: this.filters.canal,
                    linens: this.filters.linens,
                    internet: this.filters.internet,
                    name: this.propName
                }
            })
            .then(response => {
                this.numAvailable = response.data;
            })
                .catch(err => {
                    console.log(err);
                })

        },
        applyFilters() {
            this.$emit('apply-filters', this.filters, this.type, this.propName)
        },
        toggleSearch() {
            this.searchIsOpen = !this.searchIsOpen;
        },
        toggleFilters() {
            this.filterIsOpen = !this.filterIsOpen;
        },
        typeChanged(payload) {
            this.type = payload;
            this.getMatches();
        },
        locationChanged(payload) {
            this.location = payload;
            this.getMatches();
        },
        openNameSearch() {
            this.nameIsOpen = true;
            this.filterIsOpen = false;
            this.filters = {
                dock: false,
                pool: false,
                canal: false,
                internet: false,
                linens: false,
                pets: false
            }
        },
        
        searchName() {
            this.applyFilters();
            this.propName = '';
            this.nameIsOpen = false
        }
    }
}

</script>
